import React, { Fragment } from 'react';
import { details } from "../../../data/details";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faExternalLinkAlt } from '@fortawesome/free-solid-svg-icons'

import "./style.scss";


const Content = () => {
    return (
        <div className="section">
            <div className="details">
                <div className="sub-title text-center section-header">Wedding Details</div>
                <br />
                {
                    details.map((detail, i) => (
                        <div className="detail-item">
                            <div className="detail-icon">
                                {detail.icon}
                            </div>
                            <div className="detail-title">
                                {detail.key}
                            </div>
                            <div className="detail-content">
                                {
                                    detail.children ?
                                        (
                                            detail.value.map((child, ind) => (
                                                <Fragment>
                                                    <a
                                                        href={child.link}
                                                        target="_blank"
                                                        rel="noreferrer" className="detail-content-link"
                                                    >
                                                        <FontAwesomeIcon style={{ fontSize: "14px" }} icon={faExternalLinkAlt} />
                                                        {" "}
                                                        {
                                                            child.key
                                                        }
                                                    </a>
                                                    <div className="detail-content-small">{child.address}</div>
                                                    <div className="detail-content-small">{child.distance}</div>
                                                    <br />
                                                </Fragment>

                                            ))
                                        )
                                        :
                                        (
                                            <div>{detail.value}</div>
                                        )
                                }
                            </div>
                        </div>
                    ))
                }
            </div>
        </div >
    );
};

export default Content;
